[{"type": "rich_text", "value": "<p data-block-key="ru9sg">Open webapp/settings/base.py and find:</p>", "id": "0ff7c519-4cb8-4e76-95fb-65660593e00b"}, {"type": "code", "value": {"code": "DATABASES = {
