[{"type": "rich_text", "value": "<p data-block-key="o6eom">If you have content heavy pages in your Wagtail CMS and need to edit a piece of content after spotting a required change, it would be nice to be able to click a button next to the content and jump straight to it in the admin interface.</p>", "id": "76ec7179-bdf8-4cca-a5f9-0b1ec913842c"}, {"type": "callout", "value": "<p data-block-key="gu8ce">It&#x27;s similar to clicking &#x27;edit this page&#x27; in the user bar but acts more like &#x27;edit this content&#x27;</p>", "id": "3e6003cd-f9bf-44ef-afe8-5021b40da1da"}, {"type": "image_aligned_block", "value": {"image": 5, "alignment": "center", "content": "<p data-block-key="ol6q1">Checkout the pencil icons</p>"}, "id": "d3025120-7968-474e-a40c-d0d6062f18fb"}, {"type": "rich_text", "value": "<p data-block-key="sjbf3">I&#x27;ve achieved this with a template tag (simple tag) that append an edit link to a piece of content. It can be a field, streamfield or section.</p>", "id": "e6dd2287-c03a-43b8-b2e1-83599ddfa8bf"}, {"type": "code", "value": {"code": "from django import template
from django.utils.safestring import mark_safe
from django.conf import settings

default_css_field = (
    settings.C2E_CSS_FIELD if hasattr(settings, "C2E_CSS_FIELD") else "c2e-editable"
)

default_css_section = (
    settings.C2E_CSS_SECTION if hasattr(settings, "C2E_CSS_SECTION") else "c2e-editable"
)

default_css_streamfield = (
    settings.C2E_CSS_STREAMFIELD
    if hasattr(settings, "C2E_CSS_STREAMFIELD")
    else "c2e-editable absolute pull-up"
)

svg_colors = (
    settings.C2E_COLORS
    if hasattr(settings, "C2E_COLORS")
    else {
        "black": "#000000",
        "white": "#ffffff",
        "silver": "#c0c0c0",
        "red": "#ff0000",
        "green": "#00ff00",
        "blue": "#0000ff",
        "cyan": "#00ffff",
        "magenta": "#ff00ff",
        "yellow": "#ffff00",
    }
)

default_svg_color = svg_colors.get("white")

sizes = {
    "minature": 16,
    "small": 32,
    "medium": 48,
    "large": 64,
}

default_size = "small"

default_relative_parent = "true"

register = template.Library()


def pencil_icon(color, size):
    return f"""<svg xmlns="http://www.w3.org/2000/svg" height="{sizes[size]}" width="{sizes[size]}" viewBox="0 0 64 64">
    <title>pencil</title>
    <g stroke-linecap="square" stroke-width="2" fill="none" stroke="{color}" stroke-linejoin="miter"
        class="nc-icon-wrapper" stroke-miterlimit="10">
        <line data-cap="butt" x1="9" y1="43" x2="21" y2="55" stroke-linecap="butt" stroke="{color}"></line>
        <line data-cap="butt" x1="42" y1="10" x2="54" y2="22" stroke-linecap="butt" stroke="{color}"></line>
        <line data-cap="butt" x1="15" y1="49" x2="48" y2="16" stroke-linecap="butt" stroke="{color}"></line>
        <path d="M21,55,4,60,9,43,46.828,5.172a4,4,0,0,1,5.657,0l6.343,6.343a4,4,0,0,1,0,5.657Z"></path>
    </g>
</svg>"""


@register.simple_tag(takes_context=True)
def editable_field(context, value, id, field, **kwargs):
    if not context["request"].user.is_authenticated:
        return value

    color = kwargs.get("color", default_svg_color)
    size = kwargs.get("size", default_size)
    placement = kwargs.get("placement", "after")
    relative_parent = kwargs.get("relative_parent", default_relative_parent)
    classes = kwargs.get("classes", default_css_field)

    start_link = f"""<a href="/admin/pages/{id}/edit/#panel-child-content-{field}-section" class="{classes}" data-relative-parent="{relative_parent}">"""
    link_content = f"""{pencil_icon(color, size)}"""
    end_link = "</a>"

    button = start_link + link_content + end_link

    if placement == "after":
        return mark_safe(str(value) + button)

    return mark_safe(button + str(value))


@register.simple_tag(takes_context=True)
def editable_section(context, section_name, id, **kwargs):
    if not context["request"].user.is_authenticated:
        return ""

    color = kwargs.get("color", default_svg_color)
    size = kwargs.get("size", default_size)
    relative_parent = kwargs.get("relative_parent", default_relative_parent)
    classes = kwargs.get("classes", default_css_field)

    start_link = f"""<a href="/admin/pages/{id}/edit/#panel-child-content-{section_name}-section" class="{classes}" data-relative-parent="{relative_parent}">"""
    link_content = f"""{pencil_icon(color, size)}"""
    end_link = "</a>"

    button = start_link + link_content + end_link

    return mark_safe(button)


@register.simple_tag(takes_context=True)
def editable_streamfield(context, value, id, **kwargs):
    if not context["request"].user.is_authenticated:
        return value

    color = kwargs.get("color", default_svg_color)
    size = kwargs.get("size", default_size)
    placement = kwargs.get("placement", "before")
    relative_parent = kwargs.get("relative_parent", default_relative_parent)
    classes = kwargs.get("classes", default_css_streamfield)

    ret = ""
    for block in value:
        link_start = f"""<a href="/admin/pages/{id}/edit/#block-{block.id}-section" class="{classes}" data-relative-parent="{relative_parent}">"""
        link_content = f"""{pencil_icon(color, size)}"""
        link_end = "</a>"
        button = link_start + link_content + link_end

        if placement == "after":
            ret += str(block) + button
        else:
            ret += button + str(block)

    return mark_safe(str(ret))", "language": "python"}, "id": "bf70e0b3-f8bd-48e1-8a2b-63fbc12ec889"}, {"type": "rich_text", "value": "<p data-block-key="9wupc">Then in the template add the template tags in place of the regular {{ page.field_name }} tags</p>", "id": "640819cd-26ef-41f9-a509-fe33ba130cce"}, {"type": "code", "value": {"code": "{% extends "base.html" %}
{% load wagtailimages_tags wagtailcore_tags click2edit_tags %}

{% block content %}
    <div class="homepage">

        <div class="container-fluid hero">
            {% image page.image fill-1920x600 class="hero-image" alt="" %}
            <div class="hero-gradient-mask"></div>
            <div class="container">
                <div class="row">
                    <div class="col-md-6 col-md-offset-1 col-lg-5 home-hero">
                        <h1>{% editable_field page.title page.id "title" size="medium" %}</h1> {# editable_field template tag #}
                        <p class="lead">{{ page.hero_text }}{% editable_section "hero_section" page.id %}</p> {# editable_section template tag #}
                        {% if page.hero_cta_link %}
                        <a href="{% pageurl page.hero_cta_link %}" class="hero-cta-link">
                            {{ page.hero_cta }}
                        </a>
                        {% else %}
                        {{ page.hero_cta }}
                        {% endif %}
                    </div>
                </div>
            </div>
        </div>

        <div class="container">
            <div class="row promo-row">
                <div class="featured-cards col-sm-5 col-sm-offset-1">
                    {% if page.featured_section_1 %}
                    {% editable_section "featured_homepage_sections" page.id color="black" %} {# editable_section template tag #}
                    <h2 class="featured-cards__title">{{ page.featured_section_1_title }}</h2>
                    <ul class="featured-cards__list">
                        {% for childpage in page.featured_section_1.specific.children|slice:"3" %}
                        <li>
                            {% include "includes/card/listing-card.html" with page=childpage %}
                        </li>
                        {% endfor %}
                    </ul>
                    <a class="featured-cards__link" href="/breads">
                        <span>View more of our breads</span>
                        {% include "includes/chevron-icon.html" with class="featured-cards__chevron-icon" %}
                    </a>
                    {% endif %}
                </div>

                <div class="col-sm-6 promo">
                    {% editable_section "promo_section" page.id %} {# editable_section template tag #}
                    {% if page.promo_image or page.promo_title or page.promo_text %}
                        <div class="col-lg-10 promo-text">
                            {% if page.promo_title %}
                                <h2>{{ page.promo_title }}</h2>
                            {% endif %}
                            {% if page.promo_text %}
                                {{ page.promo_text|richtext }}
                            {% endif %}
                        </div>
                    {% endif %}
                    {% if page.promo_image %}
                        <figure>{% image page.promo_image fill-590x413-c100 %}</figure>
                    {% endif %}
                </div>
            </div>
        </div>

        {% if page.body %}
        <div class="container-fluid streamfield">
                <div class="row">
                    <div class="col-sm-10 col-sm-offset-1 col-md-8 col-md-offset-2 streamfield-column">
                        {% editable_streamfield page.body page.id color="black" %} {# editable_streamfield template tag #}
                    </div>
                </div>
            </div>
        {% endif %}

        <div class="container">
            <div class="row">
                <div class="col-md-12 locations-section">
                    {% if page.featured_section_2 %}
                        <h2 class="locations-section__title">{{ page.featured_section_2_title }}</h2>
                        {% for childpage in page.featured_section_2.specific.children|slice:"3" %}
                            {% include "includes/card/location-card.html" with page=childpage %}
                        {% endfor %}
                    {% endif %}
                </div>
            </div>
        </div>

        {% if page.featured_section_3 %}
            <div class="blog-section__background">
                <div class="container">
                    <div class="row">
                        <div class="col-md-12 blog-section">
                            <h2 class="blog-section__title">{{ page.featured_section_3_title }}</h2>
                            <div class="blog-section__grid">
                                {% for childpage in page.featured_section_3.specific.children|slice:"6" %}
                                    {% include "includes/card/picture-card.html" with page=childpage portrait=True %}
                                {% endfor %}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        {% endif %}
    </div>
{% endblock content %}", "language": "html"}, "id": "06997d4b-0062-4593-a17b-14a2fcd94ebf"}]

